  transaction.rollback(function(err) {
    if (err) {
      // Transaction could not be rolled back.
    }
  });
});

transaction.begin(function(err) {
  transaction.commit(function(err) {
    if (err) {
      // Transaction could not be committed.
    }
  });
});

