var log = logging.log('syslog');

log.delete(function(err, apiResponse) {
  if (!err) {
    // The log was deleted.
  }
});

var entry = log.entry('compute.googleapis.com', {
  user: 'my_username'
});

log.alert(entry, function(err, apiResponse) {});

